import React, { useEffect, useState } from 'react'
import './Home.css';
import ButtonComponent from '../ButtonComponent/ButtonComponent';
// import usersData from '../../users.json';

function Home() {
  const [user,setUser] = useState("")
  const [password,setPassword] = useState("");
  // const [isValid,setIsValid] = useState(false);

  // const validateUser = () => {
  //   const user = usersData.find(user => user.username === user && user.password === password);
  //   setIsValid(user !== undefined);
  // };

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const response = await fetch('/api/users', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ user, password })
      });
      if (response.ok) {
        console.log('User added successfully');
      } else {
        console.error('Failed to add user');
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  useEffect(() => {
    setLoading(true);
    axios
    .get('http://localhost:5555/booklist')
    .then((response) => {
      console.log(response.data)
        setBooks(response.data.data);
        setLoading(false)
    })
    .catch((error) => {
      console.log(error.message)
      setLoading(false)
    })
},[])

  // const addUserToJSON = () => {
  //   const newUser = {
  //     username: user,
  //     password: password
  //   };
  //   usersData.push(newUser);
  //   console.log(JSON.stringify(usersData, null, 2));
  // };

  // useEffect(() => {
  //   const isUserPresent = usersData.some(user => user.username === user && user.password === password);
  //   setIsValid(isUserPresent);
  //   if (!isUserPresent) {
  //     addUserToJSON();
  //   }
  // }, [user, password]);
  const handleUserChange =(event) => {
     setUser(event.target.value);
  }

  const handlePassChange = (event) => {
    setPassword(event.target.value);
  }

  // const handleSubmit = () => {
  //   console.log("clicked submit");
  // }

  return (
    <div className='cardStyle'>
      <div className='loginCard'>
        <h3 className='loginHead'>Login</h3>
        <label className='labelStyle'>Username</label><input type="text" className='userClass' value={user} onChange={handleUserChange}></input>
        <label className='labelStyle'>Password</label><input type="password" className='passwordClass' value={password} onChange={handlePassChange}></input>
        <ButtonComponent label="Submit" change={handleSubmit} disabled={!user || !password}></ButtonComponent>
      </div>
    </div>
  )

}

export default Home
