import React, { useEffect, useState } from 'react'
import './Home.css';
import ButtonComponent from '../ButtonComponent/ButtonComponent';
import usersData from './users.json';

function Home() {
  const [user,setUser] = useState("")
  const [password,setPassword] = useState("");
  const [isValid,setIsValid] = useState(false);

  const validateUser = () => {
    const user = usersData.find(user => user.username === username && user.password === password);
    setIsValid(user !== undefined);
  };

  const addUserToJSON = () => {
    const newUser = {
      username: username,
      password: password
    };
    usersData.push(newUser);
    console.log(JSON.stringify(usersData, null, 2));
  };
  useEffect(() => {
    console.log(user,password);
  },[user,password])

  useEffect(() => {
    const isUserPresent = usersData.some(user => user.username === username && user.password === password);
    setIsValidUser(isUserPresent);
    if (!isUserPresent) {
      // If user is not present, add the user to JSON file
      addUserToJSON();
    }
  }, [username, password]);
  const handleUserChange =(event) => {
     setUser(event.target.value);
  }

  const handlePassChange = (event) => {
    setPassword(event.target.value);
  }

  const handleSubmit = () => {
    console.log("clicked submit");
  }

  return (
    <div className='cardStyle'>
      <div className='loginCard'>
        <h3 className='loginHead'>Login</h3>
        <label className='labelStyle'>Username</label><input type="text" className='userClass' value={user} onChange={handleUserChange}></input>
        <label className='labelStyle'>Password</label><input type="password" className='passwordClass' value={password} onChange={handlePassChange}></input>
        <ButtonComponent label="Submit" change={handleSubmit} disabled={!user || !password}></ButtonComponent>
      </div>
    </div>
  )

}

export default Home
